import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

function CountryItem({ country, situation }) {
  return (
    <p>
      <Link to={{ pathname: '/country', state: { country } }}>{ country }</Link>
    </p>
  );
}

CountryItem.propTypes = {
  country: PropTypes.string.isRequired,
  situation: PropTypes.objectOf(PropTypes.any).isRequired,
};

const mapStateToProps = state => ({
  situation: state.cases,
});

export default connect(mapStateToProps)(CountryItem);
